	moveq	#0,d0
	move.b	interact(a1),d0
	lsl.w	#6,d0
	addi.l	#Object_RAM,d0
	movea.l	d0,a2	; a2=object
	bclr	#3,status(a2)
	bset	#4,status(a2)

